#include <iostream>
using namespace std;

int main ()
{
   // Edit Anything Except Values
   unsigned int a = 100;
   signed int b = -100;
   short c = 100;
   float d = 500.550; // Can Be use Double Or Float 
   
   // Do Not Edit
   cout << a << "\n"; // 100
   cout << b << "\n"; // -100
   cout << c << "\n"; // 100
   cout << d << "\n"; // 500.550
   return 0;
}